catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
   var ga = new GlideAjax('employeeLeaveUtility'); // employeeLeaveUtility is the script include class/name
   ga.addParam('sysparm_name', 'getLocation'); // getLocation name of function/method in our script include



   ga.addParam('sysparm_user_id', g_form.getValue('employee_leaving')); //Gets the value of employeeleaving its sys id for a particular user and stores is it in the sysparm user id


   ga.getXML(userLocation);
}


function userLocation(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    // alert(answer);
    g_form.setValue('location', answer);
////call another Glide Ajax


var ga = new GlideAjax('employeeLeaveUtility'); // employeeLeaveUtility is the script include class/name
